import React from "react";
import Box from "@material-ui/core/Box";
import Sidebar from "./Sidebar";
import Header from "./header";
import TableFooter from "@material-ui/core/TableFooter";
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexGrow: 1,
    flexDirection: "column",
    flexWrap: "nowrap",
    width: "100%",
    height: "100%"
  },
  header: {
    display: "flex",
    flexDirection: "column",
    flexWrap: "nowrap"
  },
  main: {
  flexGrow:1,
    display:"flex",
    flexDirection:"row",
    flexWrap:"nowrap",
    width:"100%",
    height:"100%",
    // [theme.breakpoints.down('sm')]: {
    //   color:'yellow'
    // },
    // "@media only screen and (max-width: 768px)": {
    //   color:'red'
    // }
  },
  sidebar: {
    display:"flex",
    width:'20%', 
    flexShrink:1, 
    color:"grey"
  },

}));


export default function Layout(props) {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <div className={classes.header}>
        <Header />
      </div>
      <div classesName={classes.main}>
        <div lassesName={classes.sidebar}>
          <Sidebar />
        </div>
        <Box display="flex" flexDirection="column" flexWrap="nowrap">
          <Box
            p={1}
            display="flex"
            flexBasis="100%"
            flexShrink={2}
            overflow="auto"
            flexGrow={1}
            bgcolor={"grey.300"}
          >
            {props.children}
          </Box>
          <Box
            display="flex"
            flexDirection="row"
            flexWrap="wrap"
            bgcolor={"grey.300"}
          >

              ТУТ должно быть оформление футера 2020 год Создатель Артём

          </Box>
        </Box>
      </div>
    </div>
  );
}
